INSERT INTO `cms` VALUES ('RTFemail_password',1352992423,'','',''),('RTFemail_password',1352992782,'Dear %receiver,\r\n\r\nYour new password is :\r\n%post\r\n\r\nPlease log in and change your password on your <a href=\"%url\">account</a> page.\r\n\r\nThank You\r\nThe %sitename Team\r\n','Here is the password you requested for %sitename',''),('RTFemail_welcome',1352999039,'','',''),('RTFemail_welcome',1352999429,'Hello %receiver,\r\n\r\nCongrats on joining %sitename! We\'re confident youok\'ll find out tools useful in simplifying and organizing your sales process. As you tour your account, be sure to visit our online tour page at <a href=\'%url1\'>%url1</a> and if any questions arise in the process, you can contact us directly or utilize our Support service at <a href=\'%url2\'>%url2</a>\r\n\r\nYour login email is %email\r\nYour password is %password\r\n\r\nRegards, \r\nThe %sitename Team\r\n<a href=\'%homeurl\'>%homeurl</a>','%sitename Registration',''),('RTFemail_invite',1352999510,'','',''),('RTFemail_invite',1352999700,'Hello %receiver\r\n\r\nYouâ€™ve been added as a %post team member on %domain. Click the link below to activate your account and begin using %sitename to help manage your customers. In the future, you can log into your account using this email address: %email\r\n\r\n<a href=\'%url\'>%url</a>\r\n\r\nRegards, \r\nThe %sitename Team\r\n<a href=\'%homeurl\'>%homeurl</a>','Set Up Your %post %sitename Account',''),('RTFemail_invite',1352999725,'Hello %receiver\r\n\r\nYou\'ve been added as a %post team member on %domain. Click the link below to activate your account and begin using %sitename to help manage your customers. In the future, you can log into your account using this email address: %email\r\n\r\n<a href=\'%url\'>%url</a>\r\n\r\nRegards, \r\nThe %sitename Team\r\n<a href=\'%homeurl\'>%homeurl</a>','Set Up Your %post %sitename Account',''),('RTFemail_invite',1352999874,'Hello %receiver\r\n\r\nYou\'ve been added as a %accountname team member on %do